event.script:
var phone = this.rawValue;
if (this.rawValue.length > 0){
if (!phone.startsWith("+")){
	xfa.host.messageBox("Телефонният номер трябва да започва с кода на съответната държава (+359*********)");
	this.rawValue = "";
	xfa.host.setFocus(this);
}

if (!phone.match(/^\+?\d*$/)) {  
	xfa.host.messageBox("Телефонният номер трябва да започва с кода на съответната държава (+359*********)");
    this.rawValue = "";
    xfa.host.setFocus(this);
}
}

